        R.drawable.kalimantan_timur,
        R.drawable.kalimantan_utara,
        R.drawable.sulawesi_utara,
        R.drawable.sulawesi_tengah,
        R.drawable.sulawesi_selatan,
        R.drawable.sulawesi_tenggara,
        R.drawable.gorontalo,
        R.drawable.sulawesi_barat,
        R.drawable.maluku,
        R.drawable.maluku_utara,
        R.drawable.papua,
        R.drawable.papua_barat
    )

    val listData : ArrayList<Provinces>
    get() {
        val list = arrayListOf<Provinces>()
        for (pos in provincesName.indices){
            val provinces = Provinces()
            provinces.name = provincesName[pos]
            provinces.capital = provincesCapital[pos]
            provinces.governor = provincesGovernor[pos]
            provinces.population = provincesPopulation[pos]
            provinces.area = provincesArea[pos]
            provinces.abstract = provincesAbstract[pos]
            provinces.logo = provincesLogo[pos]
            list.add(provinces)
        }
        return list
    }
}